        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
    }

    function _transfer(
        address token,
        uint256 shares,
        address to
    ) internal {
        IERC20(token).safeTransfer(to, shares);
    }

    function _transferBothTokens(
        address to,
        uint256 shares0,
        uint256 shares1
    ) internal {
        _transfer(token0, shares0, to);
        _transfer(token1, shares1, to);
    }

